Kategori:
 kategoriList;
private KategoriAdapter kategoriAdapter;

@Override
protected void onCreate(Bundle savedInstanceState) {
super.onCreate(savedInstanceState);
setContentView(R.layout.activity_kategori);

// Inisialisasi view
foodList = findViewById(R.id.food_list);

// Inisialisasi data kategori
kategoriList = new ArrayList<>();
    kategoriList.add(new Kategori("Makanan", R.drawable.ic_food));
    kategoriList.add(new Kategori("Minuman", R.drawable.ic_drink));

    // Inisialisasi adapter
    kategoriAdapter = new KategoriAdapter(kategoriList);

    // Set adapter ke RecyclerView
    foodList.setAdapter(kategoriAdapter);

    // Set layout manager untuk RecyclerView
    foodList.setLayoutManager(new LinearLayoutManager(this));
    }
    }

    // Adapter untuk menampilkan item kategori dalam RecyclerView
    public class KategoriAdapter extends RecyclerView.Adapter {

        private ArrayList kategoriList;

            public KategoriAdapter(ArrayList kategoriList) {
                this.kategoriList = kategoriList;
                }

                @NonNull
                @Override
                public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
                View view = LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_kategori, parent, false);
                return new ViewHolder(view);
                }

                @Override
                public void onBindViewHolder(@NonNull ViewHolder holder, int position) {
                Kategori kategori = kategoriList.get(position);
                holder.imageView.setImageResource(kategori.getImageResource());
                holder.textView.setText(kategori.getName());
                }

                @Override
                public int getItemCount() {
                return kategoriList.size();
                }

                public static class ViewHolder extends RecyclerView.ViewHolder {

                public ImageView imageView;
                public TextView textView;

                public ViewHolder(@NonNull View itemView) {
                super(itemView);
                imageView = itemView.findViewById(R.id.image_view);
                textView = itemView.findViewById(R.id.text_view);
                }
                }
                }

                // Model untuk data kategori
                public class Kategori {

                private String name;
                private int imageResource;

                public Kategori(String name, int imageResource) {
                this.name = name;
                this.imageResource = imageResource;
                }

                public String getName() {
                return name;
                }

                public int getImageResource() {
                return imageResource;
                }
                }
